var status = xhr.status
// 请求成功，调用成功回调函数
if(status >= 200 && status  }
}
}


// 格式化请求参数
function formatParams(data) {
  var arr = []
  for(var name in data) {
    arr.push(encodeURIComponent(name) + '=' + encodeURIComponent(data[name]))
  }
  // 设置随机值
  arr.push(('v=' + Math.random()).replace('.', ''))
  return arr.join('&')
}
// 使用ajax
ajax({
  url: 'http://baidu.com',
  type: 'get',
  data: {
    username:'username',
