var a = 20;
  console.log(this.a); // this -> undefined
}
foo();      // Uncaught TypeError: Cannot read property 'a' of undefined

// 非严格模式下
var a = 10;
function foo() {
  var a = 20;
    console.log(this.a); // this -> window
}
foo();    // 10
