    var interv = function(){
      // 为定义执行次数t，就会一直执行
        if(typeof t === "undefined" || t-- > 0){
            setTimeout(interv, w);
            try{
                func.call(null);
            }
            catch(e){
                t = 0;
                throw e.toString();
            }
        }
    };
setTimeout(interv, w);
};
